document.querySelector(".guess-number__number");const u=document.querySelector(".guess-number__number-input"),m=document.querySelector(".guess-number__btn-guess"),e=document.querySelector(".guess-number__message"),r=()=>Math.round(Math.random()*100);m.addEventListener("click",t=>{t.preventDefault();const n=Number(u.value),s=r();n===s?(e.textContent=`Вітаю, ви вгадали число! ${s}`,e.classList.add("guess-number__message-true"),e.classList.remove("guess-number__message-false")):(e.textContent=`Ви програли, комп'ютер загадав ${s}`,e.classList.add("guess-number__message-false"),e.classList.remove("guess-number__message-true"))});
